package com.example.frescogif.activity;

import android.graphics.Bitmap;
import android.os.Bundle;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.appcompat.app.AppCompatActivity;
import android.view.View;
import android.widget.SeekBar;

import com.bumptech.glide.request.target.SimpleTarget;
import com.bumptech.glide.request.transition.Transition;
import com.example.frescogif.GlideApp;
import com.example.frescogif.R;
import com.example.frescogif.utils.ToastUtils;
import com.example.frescogif.view.verfity.SwipeCaptchaView;

/**
 * Created by GG on 2017/12/18.
 *
 * 滑动验证码
 */

public class ScrollVerfityActivity extends AppCompatActivity implements SeekBar.OnSeekBarChangeListener, SwipeCaptchaView.OnCaptchaMatchCallback {

    private SwipeCaptchaView mSwipeCaptchaView;
    private SeekBar mSeekBar;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_scroll_verfity);
        mSwipeCaptchaView = (SwipeCaptchaView) findViewById(R.id.swipeCaptchaView);
        mSeekBar = (SeekBar) findViewById(R.id.dragBar);
        findViewById(R.id.btnChange).setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                mSwipeCaptchaView.createCaptcha();
                mSeekBar.setEnabled(true);
                mSeekBar.setProgress(0);
            }
        });


        GlideApp
                .with(this)
                .asBitmap()
                .load("data:image/jpeg;base64,")
                .error(R.mipmap.icon)
                .into(new SimpleTarget<Bitmap>() {
                    @Override
                    public void onResourceReady(@NonNull Bitmap resource, @Nullable Transition<? super Bitmap> transition) {
                        mSwipeCaptchaView.setImageBitmap(resource);
                        mSwipeCaptchaView.createCaptcha();
                        mSeekBar.setProgress(0);
                    }
                });

        mSeekBar.setOnSeekBarChangeListener(this);
        mSwipeCaptchaView.setOnCaptchaMatchCallback(this);
    }

    @Override
    public void onProgressChanged(SeekBar seekBar, int progress, boolean fromUser) {
        mSwipeCaptchaView.setCurrentSwipeValue(progress);
    }

    @Override
    public void onStartTrackingTouch(SeekBar seekBar) {
        mSeekBar.setMax(mSwipeCaptchaView.getMaxSwipeValue());
    }

    @Override
    public void onStopTrackingTouch(SeekBar seekBar) {
        mSwipeCaptchaView.matchCaptcha();
    }


    @Override
    public void matchSuccess(SwipeCaptchaView swipeCaptchaView) {
        ToastUtils.showShort(this,"验证成功");
        mSwipeCaptchaView.resetCaptcha();
        mSeekBar.setProgress(0);
    }

    @Override
    public void matchFailed(SwipeCaptchaView swipeCaptchaView) {
        ToastUtils.showShort(this,"验证失败");
        mSwipeCaptchaView.resetCaptcha();
        mSeekBar.setProgress(0);
    }
}
